package com.everybody.codes.event.year2024;

import org.junit.jupiter.api.Test;

import java.io.IOException;
import java.io.InputStream;
import java.util.Objects;
import java.util.Scanner;

import static org.assertj.core.api.Assertions.assertThat;

class Quests01Test {

    @Test
    void inputExample1() {
        String input = """
                ABBAC""";

        try (Scanner scanner = new Scanner(input)) {
            long potions = Quests01.countPotionsPartOne(scanner);

            assertThat(potions).isEqualTo(5);
        }
    }

    @Test
    void inputExample2() {
        String input = """
                AxBCDDCAxD""";

        try (Scanner scanner = new Scanner(input)) {
            long potions = Quests01.countPotionsPartTwo(scanner);

            assertThat(potions).isEqualTo(28);
        }
    }

    @Test
    void inputExample3() {
        String input = """
                xBxAAABCDxCC""";

        try (Scanner scanner = new Scanner(input)) {
            long potions = Quests01.countPotionsPartThree(scanner);

            assertThat(potions).isEqualTo(30);
        }
    }

    @Test
    void partOne() throws IOException {
        try (InputStream is = Quests01Test.class.getResourceAsStream("/2024/quests/01/input");
             Scanner scanner = new Scanner(Objects.requireNonNull(is))) {
            long potions = Quests01.countPotionsPartOne(scanner);
            assertThat(potions).isEqualTo(1289);
        }
    }

    @Test
    void partTwo() {
        String input = """
                """;

        try (Scanner scanner = new Scanner(input)) {
            long potions = Quests01.countPotionsPartTwo(scanner);

            assertThat(potions).isEqualTo(5593L);
        }
    }

    @Test
    void partThree() {
        String input = """
                """;

        try (Scanner scanner = new Scanner(input)) {
            long potions = Quests01.countPotionsPartThree(scanner);

            assertThat(potions).isEqualTo(27301L);
        }
    }

}
